package codeforces;
//https://codeforces.com/problemset/problem/467/A
import java.util.Scanner;

public class George {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
	    int a = 0,p=0;
	    int count=0;
		int f=sc.nextInt();
		for(int i=0;i<f;i++)
		{
			 a=sc.nextInt();
			 p=sc.nextInt();
			
			    
			    if(a-p>=2)
				{
					count++;
					
				}
			    
		
		}
		System.out.println(count);
		}
		}
	
		
		
		
	
	
	
		
   

